import { useSelector } from "react-redux";

const uiSlice = createSlics({
  name: "ui",
  initialState: { cartIsVisible: false },
  reducers: {
    toggle(state) {
      state.cartIsVisible !== state.cartIsVisible;
    },
  },
});
export const uiAction= uiSlice.actions
export default uiSlice
